// TODO: implement
import {GET_MESSAGES,GET_MESSAGES_FAILURE} from '../actions/messages'

const initialState = {
    messages : ['a','b']
}

export const msgReducer = (state = initialState,actions) => {
    switch (actions.type) {
        case GET_MESSAGES:
            return {
               messages : [
                   ...state.messages,
                   action.payload
               ]
            }
        default:
            return state;
    }
}
